package com.aspire.daydreaming.bundle.login;/** * @Name: LoginPresenter * @Author: yeliang * @Date: 2016/8/23 */public class LoginPresenter extends LoginContract.Presenter {    @Override    public void login(String username, String password) {        rxManager.add(mModel.login(username, password).subscribe(userModel -> {            mView.onComplete(userModel);        }, throwable -> {            mView.onError(throwable.getMessage());        }));    }}