package com.aspire.daydreaming.bundle.main;import com.aspire.daydreaming.core.app.other.AppApiImpl;import com.aspire.daydreaming.core.app.user.UserApiImpl;import com.aspire.daydreaming.core.base.RxSchedulers;import com.aspire.daydreaming.core.model.info.ActivityInfo;import com.aspire.daydreaming.core.model.info.Data;import com.aspire.daydreaming.core.model.info.ImageItem;import com.aspire.daydreaming.core.model.info._User;import rx.Observable;/** * @Name: MainModel * @Author: yeliang * @Date: 2016/8/24 */class MainModel implements MainContract.Model {    @Override    public Observable<_User> login(String username, String password) {        return new UserApiImpl().login(username,password).compose(RxSchedulers.io_main());    }    @Override    public Observable<Data<ImageItem>> getImage() {        return new AppApiImpl().getMainImages().compose(RxSchedulers.io_main());    }    @Override    public Observable<Data<ActivityInfo>> getActivityModel() {        return null;    }}