package com.aspire.daydreaming.bundle.main;import com.aspire.daydreaming.core.app.other.AppApiImple;import com.aspire.daydreaming.core.app.user.UserApiImpl;import com.aspire.daydreaming.core.base.RxSchedulers;import com.aspire.daydreaming.core.model.ActivityModel;import com.aspire.daydreaming.core.model.Data;import com.aspire.daydreaming.core.model.ImageItem;import com.aspire.daydreaming.core.model.UserModel;import rx.Observable;/** * @Name: MainModel * @Author: yeliang * @Date: 2016/8/24 */class MainModel implements MainContract.Model {    @Override    public Observable<UserModel> login(String username, String password) {        return new UserApiImpl().login(username,password).compose(RxSchedulers.io_main());    }    @Override    public Observable<Data<ImageItem>> getImage() {        return new AppApiImple().getMainImages().compose(RxSchedulers.io_main());    }    @Override    public Observable<Data<ActivityModel>> getActivityModel() {        return null;    }}