package com.aspire.daydreaming.bundle.main;/** * @Name: MainPresenter * @Author: yeliang * @Date: 2016/8/24 */class MainPresenter extends MainContract.Presenter {    @Override    public void login(String username, String password) {        rxManager.add(mModel.login(username, password).subscribe(userModel -> {            mView.onLoginComplete(userModel);        }, throwable -> {            mView.onError(throwable.getMessage());        }));    }    @Override    public void getImage() {        rxManager.add(mModel.getImage().subscribe(imageItemData -> {            mView.onGetImgComplete(imageItemData);        }, throwable -> {            mView.onError(throwable.getMessage());        }));    }    @Override    public void getActivityModel() {    }}