package com.aspire.daydreaming.bundle.myActivity;import com.aspire.daydreaming.core.app.other.AppApiImpl;import com.aspire.daydreaming.core.base.RxSchedulers;import com.aspire.daydreaming.core.model.info.ActivityInfo;import com.aspire.daydreaming.core.model.info.Data;import rx.Observable;/** * @Name: MyActivityModel * @Author: yeliang * @Date: 2016/8/24 */public class MyActivityModel implements MyActivityContract.Model {    @Override    public Observable<Data<ActivityInfo>> getActivities() {        return new AppApiImpl().getActivities("creator").compose(RxSchedulers.new_thread());    }}