package com.aspire.daydreaming.bundle.myActivity.createActivity;import com.aspire.daydreaming.core.base.BaseEntity;import com.aspire.daydreaming.core.base.BaseModel;import com.aspire.daydreaming.core.base.BasePresenter;import com.aspire.daydreaming.core.base.BaseView;import com.aspire.daydreaming.core.model.info.ActivityInfo;import com.aspire.daydreaming.core.model.leancloud.ActivityModel;import rx.Observable;/** * @Name: CreateActivityContract * @Author: yeliang * @Date: 2016/8/24 */interface CreateActivityContract {    interface View extends BaseView {        void onLoading(String msg);        void uploadImageSuccess(BaseEntity.BaseBean baseBean);        void createActivity();        void createActivitySuccess(ActivityInfo activityModel);        void onError(String errorMsg);    }    interface Model extends BaseModel {        Observable<BaseEntity.BaseBean> uploadImage(String path);        Observable<ActivityInfo> createActivity(ActivityModel activityModel);    }    abstract class Presenter extends BasePresenter<Model, View> {        public abstract void uploadImage(String path);        public abstract void createActivity(ActivityModel activityModel);        @Override        public void onStart() {        }    }}