package com.aspire.daydreaming.bundle.welcome;import android.content.Intent;import android.os.Bundle;import android.support.v7.app.AppCompatActivity;import android.text.TextUtils;import com.aspire.daydreaming.R;import com.aspire.daydreaming.bundle.login.LoginActivity;import com.aspire.daydreaming.bundle.main.MainActivity;import com.aspire.daydreaming.core.model.info._User;import com.aspire.daydreaming.core.utils.ConstantUtil;import com.aspire.daydreaming.core.utils.SharedPreferenceUtil;import java.util.Timer;import java.util.TimerTask;public class WelcomeActivity extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_welcome);        _User user = (_User) SharedPreferenceUtil.readObject(this, ConstantUtil.key.USER_INFO, ConstantUtil.fileName.USER);        Intent intent = new Intent();        if (TextUtils.isEmpty(user != null ? user.getUsername() : null)) {            intent.setClass(this, LoginActivity.class);        } else {            intent.setClass(this, MainActivity.class);        }        new Timer().schedule(                new TimerTask() {                    @Override                    public void run() {                        startActivity(intent);                        finish();                    }                }, 2000);    }}