package com.aspire.daydreaming.core.app.other;import com.aspire.daydreaming.core.base.BaseEntity;import com.aspire.daydreaming.core.model.Data;import com.aspire.daydreaming.core.model.ImageItem;import com.aspire.daydreaming.core.model.UserModel;import okhttp3.RequestBody;import retrofit2.Call;import retrofit2.http.Body;import retrofit2.http.GET;import retrofit2.http.Headers;import retrofit2.http.POST;import retrofit2.http.Path;import retrofit2.http.Query;/** * @Name: AppService * @Author: yeliang * @Date: 2016/8/23 */public interface AppService {    @GET("users")    Call<Data<UserModel>> getAllUser(@Query("skip") int skip, @Query("limit") int limit);    @GET("classes/ImageItem")    Call<Data<ImageItem>> getMainImages();    @Headers("Content-Type: image/png")    @POST("files/{name}")    Call<BaseEntity.BaseBean> upFile(@Path("name") String name, @Body RequestBody body);}