package com.aspire.daydreaming.core.app.user;import android.util.Log;import com.aspire.daydreaming.core.app.RestApi;import com.aspire.daydreaming.core.base.BaseEntity;import com.aspire.daydreaming.core.model.UserModel;import java.io.IOException;import retrofit2.Response;import retrofit2.http.Body;import retrofit2.http.Query;import rx.Observable;/** * @Name: UserApiImpl * @Author: yeliang * @Date: 2016/8/23 */public class UserApiImpl implements UserApi{    public static final String TAG = "UserApiImpl";    @Override    public Observable<UserModel> login(@Query("username") String username, @Query("password") String password) {        Observable.empty().subscribe();        return Observable.create(subscriber -> {            try {                Response<UserModel> response = RestApi.getInstance().jsonService.login(username, password).execute();                if (response.isSuccessful()) {                    subscriber.onNext(response.body());                    subscriber.onCompleted();                } else {                    Log.i(TAG, "login: " + response.code());                    Log.i(TAG, "login: " + response.isSuccessful());                    Log.i(TAG, "login: " + response.errorBody().string());                    subscriber.onError(new Throwable("用户名或密码错误"));                    subscriber.onCompleted();                }            } catch (IOException e) {                e.printStackTrace();                subscriber.onError(new Throwable("登录失败"));                subscriber.onCompleted();            }        });    }    @Override    public Observable<BaseEntity.BaseBean> createUser(@Body UserModel user) {        return null;    }}