package com.aspire.daydreaming.core.app.user;import com.aspire.daydreaming.core.base.BaseEntity;import com.aspire.daydreaming.core.model.Data;import com.aspire.daydreaming.core.model.User;import com.aspire.daydreaming.core.model.UserModel;import retrofit2.Call;import retrofit2.http.Body;import retrofit2.http.GET;import retrofit2.http.POST;import retrofit2.http.Query;/** * @Name: UserService * @Author: yeliang * @Date: 2016/8/23 */public interface UserService {    @GET("login")    Call<UserModel> login(@Query("username") String username, @Query("password") String password);    @POST("users")    Call<BaseEntity.BaseBean> createUser(@Body User user);    @GET("users")    Call<Data<UserModel>> getAllUser(@Query("skip") int skip, @Query("limit") int limit);}