package com.aspire.daydreaming.core.dagger2.module;import android.app.Activity;import android.support.v4.app.Fragment;import javax.inject.Singleton;import dagger.Module;import dagger.Provides;/** * @Name: ApiModule * @Author: yeliang * @Date: 2016/8/23 */@Modulepublic class ApiModule {    private Activity mActivity;    private Fragment mFragment;    public ApiModule(Activity mActivity) {        this.mActivity = mActivity;    }    public ApiModule(Fragment mFragment) {        this.mFragment = mFragment;    }    @Provides    @Singleton    public Activity provideActivity() {        return mActivity;    }    @Provides    @Singleton    public Fragment provideFragment() {        return mFragment;    }}