package com.aspire.daydreaming.core.dagger2.module;import android.app.Application;import com.aspire.daydreaming.core.app.DDApplication;import javax.inject.Singleton;import dagger.Module;import dagger.Provides;/** * @Name: AppModule * @Author: yeliang * @Date: 2016/8/23 */@Modulepublic class AppModule {    private DDApplication mApplication;    public AppModule(DDApplication mApplication) {        this.mApplication = mApplication;    }    @Provides    @Singleton    protected Application provideApplication() {        return mApplication;    }}