package com.aspire.daydreaming.core.model.leancloud;import com.aspire.daydreaming.core.base.BaseEntity;import com.aspire.daydreaming.core.model.info.Pointer;/** * @Name: ActivityInfo * @Author: yeliang * @Date: 2016/8/26 */public class ActivityModel extends BaseEntity.BaseBean {    private Pointer creator;    private String title;    private String content;    public ActivityModel(Pointer creator, String title, String content) {        this.creator = creator;        this.title = title;        this.content = content;    }    public Pointer getCreator() {        return creator;    }    public void setCreator(Pointer creator) {        this.creator = creator;    }    public String getTitle() {        return title;    }    public void setTitle(String title) {        this.title = title;    }    public String getContent() {        return content;    }    public void setContent(String content) {        this.content = content;    }    @Override    public String toString() {        return "ActivityInfo{" +                "creator=" + creator +                ", title='" + title + '\'' +                ", content='" + content + '\'' +                '}';    }}