package com.aspire.daydreaming.core.utils;import android.app.Activity;import android.util.DisplayMetrics;/** * @Name: BaseTools * @Author: yeliang * @Date: 2016/8/24 */public class BaseTools {    public static int getWindowsWidth(Activity activity){        DisplayMetrics displayMetrics = new DisplayMetrics();        activity.getWindowManager().getDefaultDisplay().getMetrics(displayMetrics);        return displayMetrics.widthPixels;    }    public static int getWindowsHeight(Activity activity){        DisplayMetrics displayMetrics = new DisplayMetrics();        activity.getWindowManager().getDefaultDisplay().getMetrics(displayMetrics);        return displayMetrics.heightPixels;    }}