package com.aspire.daydreaming.core.widget;import android.content.Context;import android.support.v4.view.ViewPager;import android.util.Log;import android.view.View;import android.widget.PopupWindow;import android.widget.TextView;import com.aspire.daydreaming.R;import java.util.List;import butterknife.Bind;import butterknife.ButterKnife;/** * @Name: CustomPopupWindow * @Author: yeliang * @Date: 2016/8/26 */public class CustomPopupWindow extends PopupWindow {    public static final String TAG = "CustomPopupWindow";    @Bind(R.id.tv_position)    TextView mTvPosition;    @Bind(R.id.tv_total)    TextView mTvTotal;    @Bind(R.id.vp_image)    ViewPager mVpImage;    private List<String> mUrls;    private int mPosition = 0;    private Context mContext;    private View mView;    public CustomPopupWindow(View contentView, int width, int height, boolean focusable, Context context, List<String> urls, int position) {        super(contentView, width, height, focusable);        mView = contentView;        mUrls = urls;        mPosition = position;        mContext = context;        init(mContext);    }    private void init(Context context) {        ButterKnife.bind(this, mView);        mVpImage.setAdapter(new CustomPagerAdapter(context, mUrls));        mVpImage.setOnClickListener(view -> {            Log.i(TAG, "init: " + view.getId());            this.dismiss();        });        mTvTotal.setText(String.valueOf(mUrls.size()));        mTvPosition.setText(String.valueOf(mPosition));    }}